using namespace sf;

extern sf::Vector2f x;

CoordStruct Abs_Cell::GetCoord() const {//LOGFUNC;
	return LocCoord;
}

Abs_Cell::Abs_Cell(int x, int y, bool Special) : LocCell(x, y), isSpecial(Special) {LOGFUNC;
	renderTexture = &TestManger::GetInstance().testTerrainTexture;
	renderSprite.setTexture(*renderTexture);
	size.x = renderTexture->getSize().x;
	size.y = renderTexture->getSize().y;
	renderSprite.setOrigin(size.x / 2.0, size.y / 2.0);
	LocCoord = CoordStruct(LocCell.x * DIVS, LocCell.y * DIVS, height);

	_coordObj[0] = -LocCell.x * DIVS;
	_coordObj[1] = -LocCell.y * DIVS;
	_coordObj[2] = -height;
	_coordObj[3] = 1;

	occTest_physhape = new btBoxShape(btVector3(btScalar(DIVS/2), btScalar(DIVS/2), btScalar(DIVS/2)));
	btTransform trans = btTransform::getIdentity();
	trans.setOrigin(btVector3(btScalar(LocCoord.x), btScalar(LocCoord.y), btScalar(DIVS/2)));
	occTest_mo = new btDefaultMotionState(trans);
	btRigidBody::btRigidBodyConstructionInfo info(0, occTest_mo, occTest_physhape, btVector3(0, 0, 0));

	trans = btTransform::getIdentity();
	trans.setOrigin(btVector3(btScalar(LocCoord.x), btScalar(LocCoord.y), btScalar(0)));
	sphereTest_mo = new btDefaultMotionState(trans);
	// occTestBody = new btRigidBody(info);
}

void Abs_Cell::Render() {
	// renderSprite.setOrigin(30, 15);
	GetViewPos_Opted(_coordObj);
	renderSprite.setPosition(x);
	if (ShowCenter) renderSprite.setColor(Color(255, 255, 255, 128));
	// if (!PassableTo()) renderSprite.setColor(Color(0, 0, 0, 255));
	if (x.x > (-size.x) && x.x <= (WIDTH+size.x) && x.y > (-size.y) && x.y <= (HEIGHT+size.y)) {
		TestManger::GetInstance().window->draw(renderSprite);
		// if (!PassableTo()) {
		// 	CircleShape shape(5);
		// 	shape.setFillColor(Color(255, 0, 0));
		// 	shape.setPosition(x);
		// 	TestManger::GetInstance().window->draw(shape);
		// }
	}

	if (ShowCenter) {
		CircleShape shape(1);
		if (isSpecial) shape.setFillColor(Color(255, 0, 0));
		else shape.setFillColor(Color(0, 0, 0));
		shape.setPosition(x);
		TestManger::GetInstance().window->draw(shape);
	}
}

